mapper.insert:
INSERT 
INTO 
apply_user
(usemail
, bsbno
, status) VALUE (
#{usEmail}
, #{bsBno}
, 'D'
)

mapper.update:
UPDATE
apply_user
SET status = #{status}
WHERE usemail = #{usEmail}
AND bsbno = #{bsBno}

mapper.delete:
DELETE
FROM
apply_user
WHERE usemail = #{usEmail}
AND bsbno = #{bsBno}
