mapper.update:
UPDATE 
board_study
SET now = now + #{amount}
WHERE bno = #{bno}

mapper.insert:
insert into board_study (
title
, writer
, max
, rDId
, rSId
, age
, sc
, sd
, st
, et
, content
) values (
#{title}
, #{writer}
, #{max}
, #{rDId}
, #{rSId} 
, #{age}
, #{sc}
, #{sd}
, #{st}
, #{et}
, #{content} 
)

INSERT INTO
board_category (
bsbno
, categoryd
, categorys
) VALUES (
#{bno}
, #{categoryD}
, #{categoryS}
)

INSERT
INTO
board_attach(
name
, bsBno
, status
)
VALUES(
#{name}
, #{bno}
, #{status}
)

mapper.select:
select cSName, csId
 from study_category
 where cdId=#{csId}

select rSName, rSId
 from region
where rDId=#{rSId}
